function show_chat_popup() {
    let messages = [
        {role: "system", content: "You are an ERPNext assistant."} // system prompt
    ];

    let d = new frappe.ui.Dialog({
        title: 'Ask AI',
        fields: [
            {
                label: 'Your Question',
                fieldname: 'user_input',
                fieldtype: 'Small Text'
            },
            {
                label: 'ChatGPT Response',
                fieldname: 'gpt_response',
                fieldtype: 'HTML',
                read_only: 1
            }
        ],
        primary_action_label: 'Ask',
        primary_action(values) {
            if (!values.user_input) return;

            // Add user message to messages array
            messages.push({role: "user", content: values.user_input});

            frappe.call({
                method: "his.api.chatgpt.ask_gpt",
                args: { messages: messages }, // send full conversation
                callback: function(r) {
                    if (r.message) {
                        // Add assistant response to messages array
                        messages.push({role: "assistant", content: r.message});
                        // Show latest response
                        d.set_value('gpt_response', `<div>${r.message}</div>`);
                    }
                }
            });

            // Clear input
            d.set_value('user_input', '');
        }
    });
    d.show();
}

function askChatGPT(prompt, dialog) {
    frappe.call({
        method: "his.api.chatgpt.ask_gpt",  // We'll make this next
        args: { prompt: prompt },
        callback: function(r) {
            if (r.message) {
                dialog.set_value('gpt_response', `<div>${r.message}</div>`);
            }
        }
    });
}
