function calculateTotalUnits(frm) {
    var total_units = 0;
    frm.doc.accounts.forEach(function(row) {
        total_units += row.amount;
    });

    // Set the total sum in the field you want to update
    frm.set_value('total', total_units);

   frm.set_value('difference', frm.doc.total -frm.doc.balance  );



}

