frappe.ui.form.on('Vital Signs', {
    height: function(frm) {
        calculate_bmi(frm);
    },
    weight: function(frm) {
        calculate_bmi(frm);
    }
});

function calculate_bmi(frm) {
    if (frm.doc.height && frm.doc.weight) {
        let height_in_meters = frm.doc.height;
        let weight_in_kg = frm.doc.weight;

        // Calculate BMI
        let bmi = weight_in_kg / (height_in_meters * height_in_meters);
        frm.set_value('bmi', bmi.toFixed(2));

        // Determine weight status
        let weight_status = '';
        if (bmi < 18.5) {
            weight_status = 'Underweight';
        } else if (bmi >= 18.5 && bmi < 24.9) {
            weight_status = 'Normal';
        } else if (bmi >= 25 && bmi < 29.9) {
            weight_status = 'Overweight';
        } else {
            weight_status = 'Obesity';
        }

        frm.set_value('weight_status', weight_status);
    }
}

