	},
	from_date: function(frm){
	     var child_table_fieldname = 'items';
		var child_table_data = frm.doc[child_table_fieldname] || [];
		for (var i = child_table_data.length - 1; i >= 0; i--) {
			var child_row = child_table_data[i];
			frappe.model.clear_table(frm.doc, child_table_fieldname, child_row.name);
		}
		frm.refresh_fields();
	    if (frm.doc.patient){

	        frappe.call({
                method: "his.his.doctype.sales_return.sales_return.get_billed_items",
                args: {
                    "patient": frm.doc.patient,
                    "from_date": frm.doc.from_date,
                    "to_date" : frm.doc.to_date

                },
                callback: function(response) {
                    console.log(response.message);
                    // Process fetched sales invoices and their items here
                    frm.set_value("items", response.message)
                }

	        })    
	    }
	    else{
	        frappe.msgprint("Please Select Patient First")
	         frm.set_value("from_date","")
	    }

	}
	,

		to_date: function(frm){
	     var child_table_fieldname = 'items';
		var child_table_data = frm.doc[child_table_fieldname] || [];
		for (var i = child_table_data.length - 1; i >= 0; i--) {
			var child_row = child_table_data[i];
			frappe.model.clear_table(frm.doc, child_table_fieldname, child_row.name);
		}
		frm.refresh_fields();
	    if (frm.doc.patient && frm.doc.from_date){

	        frappe.call({
                method: "his.his.doctype.sales_return.sales_return.get_billed_items",
                args: {
                    "patient": frm.doc.patient,
                    "from_date": frm.doc.from_date,
                    "to_date" : frm.doc.to_date

                },
