frappe.ui.form.on('Normal Test Result', {
    select_result: function(frm, cdt, cdn) {
        var row = locals[cdt][cdn]  // Retrieve the document row
        console.log(row)
        row.result_value = row.select_result;  // Set the result_value based on select_result

	   frm.refresh_field("normal_test_items") // Refresh the normal_test_result field
    },

    // 	-------------------flag-----------------------------------
   result_value: function(frm, cdt, cdn) {
    var row = locals[cdt][cdn];
    let result = parseFloat(row.result_value);

    if (isNaN(result)) {
        console.warn("Invalid result_value:", row.result_value);
        return;
    }

    if (row.normal_range) {
        let normal_range = row.normal_range.trim();

        if (normal_range.startsWith("<")) {
            let max = parseFloat(normal_range.replace(/[^0-9.]/g, ''));
            if (!isNaN(max) && result > max) {
                row.flag = "";
            } else {
                row.flag = "";
            }

        } else if (normal_range.startsWith(">")) {
            let min = parseFloat(normal_range.replace(/[^0-9.]/g, ''));
            if (!isNaN(min) && result < min) {
                row.flag = "";
            } else {
                row.flag = "";
            }

        } else if (normal_range.includes("-")) {
            let normal = normal_range.split("-").map(val => val.trim());
            let low = parseFloat(normal[0]);
            let high = parseFloat(normal[1]);

            if (!isNaN(low) && !isNaN(high)) {
                if (result < low) {
                    row.flag = "L";
                } else if (result > high) {
                    row.flag = "H";
                } else {
                    row.flag = "";
                }
            }
        }

        frm.refresh_field("normal_test_items");
    }
}
