 before_save: function(frm) {
        let promises = [];

        frm.doc.accounts.forEach(row => {
            let doctype, fieldname;

            if (row.party_type == "Employee") {
                doctype = "Employee";
                fieldname = "employee_name";
            } else if (row.party_type == "Customer") {
                doctype = "Customer";
                fieldname = "customer_name";
            } else if (row.party_type == "Supplier") {
                doctype = "Supplier";
                fieldname = "supplier_name";
            }

            if (doctype) {
                let promise = frappe.db.get_value(doctype, row.party, fieldname)
                    .then(r => {
                        if (r.message) {
                            frappe.model.set_value(row.doctype, row.name, "party_name", r.message[fieldname]);
                        }
                    });
                promises.push(promise);
            }
        });

        return Promise.all(promises); // Ensures all async updates complete before saving
    },
	before_submit: function(frm){
	    if (!frm.doc.user_remark){
	        frappe.throw("Please Enter User Remarks!!")
	    }
	}
})

