function apply_membership_discount(frm) {
    if (!frm.doc.patient || frm.doc.so_type !== "Cashiers") return;

    // Fetch discount percentage and membership status from Patient
    frappe.db.get_value("Patient", frm.doc.patient, ["percentage", "is_membership"])
        .then(r => {
            if (!r.message) return;
            const { percentage, is_membership } = r.message;
            
            if (is_membership === "Membership" && percentage > 0) {
                frm.set_value("additional_discount_percentage", percentage);
            }
        });
}
