html:
{% set printed_departments = [] %}
{% if doc.lab_test %}
    {% for samp in doc.lab_test %}
        {% set dept = (samp.department or '').strip().upper() %}
        {% if dept not in printed_departments %}
            {% set _ = printed_departments.append(dept) %}

            <div class="main_qr" style="margin: 10px 0px;">
                <div class="text">
                    <p>
                        <span style="padding-left: 4px; font-size: 10px; white-space: nowrap; width:100%;">
                            {{ doc.patient_name or '' }}
                        </span><br />
                        <span style="padding-left: 4px; font-size: 10px;">
                            {{ doc.patient or '' }} /Lab-{{ doc.lab_ref or '' }}
                        </span><br>
                    </p>
                </div>
                <img src="/files/barcode.png" alt="qrcodePic" />
                <span style="text-align: center; width: 100px; margin-left: 16px; line-height: 1; font-size: 10px;">
                    {{ samp.department or "" }} /NO: {{ doc.token_no }}
                </span>
            </div>

        {% endif %}
    {% endfor %}
{% else %}
    <div class="page">
        <div class="main_qr">
            <div class="text">
                <p>
                    <span style="padding-left: 0px; font-size:7px;">{{ doc.patient_name or '' }}</span><br />
                    <span style="padding-left: 43px;">{{ doc.patient or '' }}</span>
                </p>
            </div>
            <img src="/files/barcode.png" alt="qrcodePic" />
            <span style="padding-left: 30px; padding-top:1px;">Lab-{{ doc.lab_ref or '' }}</span>
        </div>
    </div>
{% endif %}

{% set departments = {} %}
{% set tests = {} %}

{% for item in doc.items %}

{% set template = frappe.get_doc("Lab Test Template" , item.item_code ) %}
{% set x= departments.__setitem__(template.department ,template.department )  %}

{% endfor %}

{% for dep in departments %}

<h1>{{dep}}</h1>

{% endfor %}

