@import url('https://fonts.googleapis.com/css2?family=Lato:wght@100;400;900&display=swap');

:root {
  --primary: #0000ff;
  --secondary: #3d3d3d; 
  --white: #fff;
}

*{
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: 'Lato', sans-serif;
}

.title_wrap{
	text-transform: uppercase;
	font-size: 14px;
	color: #000;
}

.printed{
	display: flex;
	justify-content: space-between;
	padding: 2px 0 0px;
	align-items: flex-start;
	font-weight: 900;
}

.footer > p{
	text-align: center;
	color: #000;
	text-decoration: underline;
	font-size: 14px;
	padding-bottom: 2px;
}

.bold{
	font-weight: 900;
}

.light{
	font-weight: 100;
}

.wrapper{
	background: var(--white);
	/*align-items :  center;*/
	/*padding: 10px;*/
}

.invoice_wrapper{
	/* border: 3px solid var(--primary); */
	width: 100%;
	max-width: 100%;
}

.invoice_wrapper .header .logo_invoice_wrap,
.invoice_wrapper .header .bill_total_wrap{
	display: flex;
	justify-content: space-between;
	/*padding: 5px;*/
}

.invoice_wrapper .header .logo_sec{
	display: flex;
	align-items: center;
}

.invoice_wrapper .header .logo_sec .title_wrap{
	margin-left: 5px;
}

.invoice_wrapper .header .logo_sec .title_wrap .title{
	text-transform: uppercase;
	font-size: 14px;
	color: var(--primary);
}
.title_sec{
	font-size: 14px;
	border-bottom: 2px solid #000;
	width: 300px;
	text-align: center;
	font-family: Arial, Helvetica, sans-serif;

	/*letter-spacing: 5px; */
}
.info{
	display: flex;
	justify-content: flex-end;
	margin-top: 2px;

}

.inv_name , .inv_date{
	border-right: 1px solid #000;
	padding: 0 20px;
	/* flex-grow: 0; */
}

.info p {
	text-align: center;
}

.invoice_wrapper .header .logo_sec .title_wrap .sub_title{
	font-size: 12px;
}

.invoice_wrapper .header .invoice_sec,
.invoice_wrapper .header .bill_total_wrap .total_wrap{
	text-align: right;
	width: 300px;
}

.invoice_wrapper .header .invoice_sec .invoice{
	font-size: 18px;
	color: var(--primary);
}

.invoice_wrapper .header .invoice_sec .invoice_no,
.invoice_wrapper .header .invoice_sec .date{
	display: flex;
	width: 100%;
}

.invoice_wrapper .header .invoice_sec .invoice_no span:first-child,
.invoice_wrapper .header .invoice_sec .date span:first-child{
	width: 70px;
	text-align: left;
}

.invoice_wrapper .header .invoice_sec .invoice_no span:last-child,
.invoice_wrapper .header .invoice_sec .date span:last-child{
	width: calc(100% - 70px);
}

.invoice_wrapper .header .bill_total_wrap .total_wrap .price,
.invoice_wrapper .header .bill_total_wrap .bill_sec .name{
	color: var(--primary);
	font-size: 20px;
}
.invoice_wrapper .body .main_table {
    margin-top: 8px;
    border-top : 1px solid #000;
}
.invoice_wrapper .body .main_table .table_header{
	/* background: var(--primary); */
	/* color: #000 !important; */
}

.invoice_wrapper .body .main_table .table_header .myrow{
	color: var(--secondary);
	font-size: 14px;
	border-bottom: 0px;	
	border-bottom: 1px solid var(--secondary);
	/* border-top: 1px solid var(--secondary); */

}

.invoice_wrapper .body .main_table .myrow{
	display: flex;
	border-bottom: 1px solid var(--secondary);
}

.invoice_wrapper .body .main_table .myrow .col{
	padding: 2px;
}
.invoice_wrapper .body .main_table .myrow .col_no{width: 5%;}
.invoice_wrapper .body .main_table .myrow .col_des{width: 45%;}
.invoice_wrapper .body .main_table .myrow .col_price{width: 20%; text-align: center;}
.invoice_wrapper .body .main_table .myrow .col_qty{width: 10%; text-align: center;}
.invoice_wrapper .body .main_table .myrow .col_total{width: 20%; text-align: right;}

.invoice_wrapper .body .paymethod_grandtotal_wrap{
	display: flex;
	justify-content: flex-end;
	padding: 2px 0 30px;
	align-items: flex-end;
}

.invoice_wrapper .body .paymethod_grandtotal_wrap {
	padding-left: 30px;
}

/*.paymethod_sec{*/
/*	padding-left: 5px;*/
/*}*/

.invoice_wrapper .body .paymethod_grandtotal_wrap .grandtotal_sec{
	width: 40%;
}

.invoice_wrapper .body .paymethod_grandtotal_wrap .grandtotal_sec p{
	display: flex;
	width: 100%;
	padding-bottom: 2px;
	justify-content: flex-end;
	border-bottom: 1px solid #000;
}

.invoice_wrapper .body .paymethod_grandtotal_wrap .grandtotal_sec p span{
	padding: 0 10px;
}

.invoice_wrapper .body .paymethod_grandtotal_wrap .grandtotal_sec p span:first-child{
	width: 60%;
}

.invoice_wrapper .body .paymethod_grandtotal_wrap .grandtotal_sec p span:last-child{
	width: 40%;
	text-align: left;
}

.invoice_wrapper .body .paymethod_grandtotal_wrap .grandtotal_sec p:last-child{
	border-bottom: none;

}

.invoice_wrapper .footer{
	padding: 10px;
}

.invoice_wrapper .footer > p{
	/* color: var(--primary); */
	text-decoration: none;
	font-size: 14px;
	padding-bottom: 2px;
}

.invoice_wrapper .footer .terms .tc{
	font-size: 14px;
}

