        frappe.throw("Service Orders already Billed so it can't change it!!!")
if  doc.medication_so:
    sales_order_medication = frappe.get_doc("Sales Order", doc.medication_so)

    if sales_order_medication.status == "Completed":
        frappe.throw("Pharmacy Orders already Billed so it can't change it!!!")

if doc.total_taxes_and_charges and doc.is_pos:
    # Subtract the tax amount from the first payment row
    if doc.payments:
        # Assuming the first row is the sales amount row
        first_payment = doc.payments[0]
        first_payment.amount = doc.net_total

        # Check if a VAT payment row already exists
        vat_exists = False
        for payment in doc.payments:
            if payment.mode_of_payment == "VAT":
                vat_exists = True
                break

        # Append the VAT payment row only if it doesn't exist
        if not vat_exists:
            doc.append("payments", {
                "mode_of_payment": "VAT",
                "amount": doc.total_taxes_and_charges
            })
    else:
        frappe.errprint("Error: No payments found to update.")

if doc.percentage:
